import React, { useState, useEffect } from 'react';
import { getResumes, getResume } from '../services/api';
import ResumeDetails from './ResumeDetails';
import './PastResumesTable.css';

const PastResumesTable = () => {
  const [resumes, setResumes] = useState([]);
  const [selectedResume, setSelectedResume] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    fetchResumes();
  }, []);

  const fetchResumes = async () => {
    try {
      const response = await getResumes();
      setResumes(response.data);
    } catch (err) {
      setError('Failed to fetch resumes');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleViewDetails = async (id) => {
    try {
      const response = await getResume(id);
      setSelectedResume(response.data);
      setModalOpen(true);
    } catch (err) {
      setError('Failed to fetch resume details');
      console.error(err);
    }
  };

  const closeModal = () => {
    setModalOpen(false);
    setSelectedResume(null);
  };

  if (loading) return <div className="loading">Loading resumes...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="past-resumes">
      <h2>Previously Analyzed Resumes</h2>
      
      {resumes.length === 0 ? (
        <p>No resumes analyzed yet.</p>
      ) : (
        <table className="resumes-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>File Name</th>
              <th>Uploaded At</th>
              <th>Rating</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {resumes.map((resume) => (
              <tr key={resume.id}>
                <td>{resume.name || 'N/A'}</td>
                <td>{resume.email || 'N/A'}</td>
                <td>{resume.phone || 'N/A'}</td>
                <td>{resume.file_name}</td>
                <td>{new Date(resume.uploaded_at).toLocaleString()}</td>
                <td>{resume.resume_rating || 'N/A'}/10</td>
                <td>
                  <button 
                    onClick={() => handleViewDetails(resume.id)}
                    className="details-button"
                  >
                    Details
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      
      {modalOpen && selectedResume && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="close-button" onClick={closeModal}>Ã—</button>
            <ResumeDetails data={selectedResume} />
          </div>
        </div>
      )}
    </div>
  );
};

export default PastResumesTable;
